// DistributorTable.js

import React, { useState } from 'react';
import DistributorCard from './DistributorCard';

// ===== MOCK DATA =====
const mockDistributors = [
  { id: 1, name: 'Alpha Distributors', status: 'On Track', sales: 50000 },
  { id: 2, name: 'Beta Supply Co.', status: 'At Risk', sales: 15000 },
  { id: 3, name: 'Gamma Wholesale', status: 'Overperforming', sales: 90000 },
  { id: 4, name: 'Delta Traders', status: 'On Track', sales: 30000 },
  { id: 5, name: 'Epsilon Logistics', status: 'At Risk', sales: 10000 },
  { id: 6, name: 'Zeta Corp', status: 'Overperforming', sales: 120000 },
];

function DistributorTable({ distributors = mockDistributors }) {
  const [statusFilter, setStatusFilter] = useState('All'); // All | At Risk | On Track | Overperforming
  const [minSales, setMinSales] = useState(0); // Sales threshold filter

  // Apply both filters together
  const filteredDistributors = distributors.filter(distributor => {
    const statusMatch = statusFilter === 'All' || distributor.status === statusFilter;
    const salesMatch = distributor.sales >= minSales;
    return statusMatch && salesMatch;
  });

  return (
    <div className="w-full p-6 space-y-4">
      {/* === Filter Buttons === */}
      <div className="flex flex-wrap items-center gap-4 mb-4">
        <div className="flex space-x-2">
          {['All', 'At Risk', 'On Track', 'Overperforming'].map(status => (
            <button
              key={status}
              onClick={() => setStatusFilter(status)}
              className={`px-3 py-1 rounded-lg text-sm font-medium shadow-sm transition ${
                statusFilter === status
                  ? 'bg-blue-600 text-white'
                  : 'bg-gray-200 text-gray-700 hover:bg-blue-100'
              }`}
            >
              {status}
            </button>
          ))}
        </div>

        {/* === Sales Range Filter === */}
        <div className="flex items-center space-x-2">
          <label htmlFor="minSales" className="text-sm font-medium text-gray-700">
            Min Sales:
          </label>
          <input
            id="minSales"
            type="range"
            min="0"
            max="150000"
            step="1000"
            value={minSales}
            onChange={(e) => setMinSales(Number(e.target.value))}
            className="w-40"
          />
          <span className="text-sm font-semibold text-gray-800">${minSales.toLocaleString()}</span>
        </div>
      </div>

      {/* === Distributor Cards Grid === */}
      {filteredDistributors.length > 0 ? (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredDistributors.map((distributor) => (
            <DistributorCard key={distributor.id} distributor={distributor} />
          ))}
        </div>
      ) : (
        <div className="text-center text-gray-500 py-10 text-sm">
          No distributors match the selected filters.
        </div>
      )}
    </div>
  );
}

export default DistributorTable;
